function loadMenu() {
  fetch('/menu.html')
    .then(r => r.text())
    .then(html => {
      const nav = document.createElement('nav');
      nav.setAttribute('role', 'navigation');
      nav.innerHTML = html;
      document.body.prepend(nav);
      // Acessibilidade: links
      nav.querySelectorAll('a').forEach(a => {
        a.setAttribute('tabindex', '0');
      });
      // Botão hambúrguer
      const btn = nav.querySelector('.fekf-menu-button');
      if (btn) {
        btn.setAttribute('aria-expanded', 'false');
        btn.addEventListener('click', function() {
          const wrap = nav.querySelector('.fekf-menu-wrap');
          const expanded = wrap.classList.toggle('open');
          btn.setAttribute('aria-expanded', expanded);
          if (expanded) nav.querySelector('a,button').focus();
        });
        // Tecla Esc fecha menu
        document.addEventListener('keydown', function(e) {
          if (e.key === 'Escape') {
            const wrap = nav.querySelector('.fekf-menu-wrap');
            if (wrap && wrap.classList.contains('open')) {
              wrap.classList.remove('open');
              btn.setAttribute('aria-expanded', 'false');
              btn.focus();
            }
          }
        });
      }
    });
}
function loadFooter() {
  fetch('/footer.html')
    .then(r => r.text())
    .then(html => {
      const footer = document.createElement('div');
      footer.innerHTML = html;
      document.body.appendChild(footer);
    });
}
document.addEventListener('DOMContentLoaded', function() {
  loadMenu();
  loadFooter();
  fadeInMainContent();
  setupDarkModeButton();
  applySavedDarkMode();
});
